def getSearchPhrases():        import tkinter as tk            root = tk.Tk()    root.title("Search Words")        label1 = tk.Label(root, text='Words/phrases to search for in selected processed corpus (separated by comma)')    label1.pack(padx=5, pady=5)    entry1 = tk.Entry(root, width=50)    entry1.pack(padx=5, pady=5)        check_var = tk.BooleanVar()    checkbox = tk.Checkbutton(root, text="In addition to excel, save outputs as json", variable=check_var)    checkbox.pack(padx=5, pady=5)        def after_button_click():        global input_text1, save_json        input_text1 = entry1.get()        save_json = check_var.get()        root.destroy()     button = tk.Button(root, text="Go!", command=after_button_click)    button.pack(padx=5, pady=5)        root.mainloop()        return input_text1, save_json